import random
import string

#Defined Function that analyzes original password
def analyze_password(current_password):
    has_upper = any(c.isupper() for c in current_password)
    has_lower = any(c.islower() for c in current_password)
    has_symbols = any(c in string.punctuation for c in current_password)
    has_numbers = any(c.isdigit() for c in current_password)
    return len(current_password), has_upper, has_lower, has_symbols, has_numbers

#character pool for stronger password creation
def generate_strong_password(current_password):
    length, has_upper, has_lower, has_symbols, has_numbers = analyze_password(current_password)
    character_pool = ""
    if has_lower:
        character_pool += string.ascii_lowercase
    if has_upper:
        character_pool += string.ascii_uppercase
    if has_symbols:
        character_pool += string.punctuation
    if has_numbers:
        character_pool += string.digits

    secure_password_list = []

    #creates a list and includes the characters from the current password
    for char in current_password:
        if char.islower():
            secure_password_list.append(random.choice(string.ascii_lowercase))
        elif char.isupper():
            secure_password_list.append(random.choice(string.ascii_uppercase))
        elif char.isdigit():
            secure_password_list.append(random.choice(string.digits))
        elif char in string.punctuation:
