        # If the character is a lowercase letter, change it to an uppercase letter
        if char.islower():
            new_password += char.upper()
        # If the character is an uppercase letter, change it to a lowercase letter
        elif char.isupper():
            new_password += char.lower()
        # If the character is a digit, change it to the opposite case
        elif char.isdigit():
