import React, { useState } from "react";

const PassWordEntry = () => {
  const [password, setPassword] = useState("");
  const [showPrompt, setShowPrompt] = useState(false);

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowPrompt(true);
  };

  const handleCreateNewPassword = () => {
    // Here you can implement logic to handle creating a new password
    // For simplicity, we'll just log the new password to console
    console.log("New password created:", password);
    handlePromptClose(); // Close the prompt after handling
  };

  const handlePromptClose = () => {
    setShowPrompt(false);
    setPassword(""); // Reset password field after closing prompt
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <form className="magic" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                value={password}
                onChange={handlePasswordChange}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>

      {/* Render password prompt dialog using conditional rendering */}
      {showPrompt && (
        <div className="password-prompt">
          <h2>Create New Password</h2>
          <p>Your entered password: {password}</p>
          <button className="btn btn-primary" onClick={handleCreateNewPassword}>
            Create Password
          </button>
          <button className="btn btn-secondary" onClick={handlePromptClose}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default PassWordEntry;
