import React, { useState } from "react";

const PassWordEntry = () => {
  const [password, setPassword] = useState("");

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Prompt the user to confirm creating a new password
    const shouldCreateNewPassword = window.confirm(
      `Do you want to create a new password based on "${password}"?`
    );

    if (shouldCreateNewPassword) {
      // Implement your logic here to create a new password
      console.log("New password created:", password);

      // Optionally clear the password field after creating a new password
      setPassword("");
    } else {
      // Clear the password field if the user cancels creating a new password
      setPassword("");
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <form className="magic" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Enter password"
                value={password}
                onChange={handlePasswordChange}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PassWordEntry;
