import React, { useState } from "react";

const PassWordEntry = () => {
  const [currentPassword, setCurrentPassword] = useState("");

  const handlePasswordChange = (e) => {
    setCurrentPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Show confirmation dialog to create a new password
    const confirmed = window.confirm(
      `Are you sure you want to create a new password based on "${currentPassword}"?`
    );

    if (confirmed) {
      // Implement your logic to create a new password
      console.log("New password created based on:", currentPassword);

      // Clear the current password field after processing
      setCurrentPassword("");
    }
  };

  return (
    <>
      <p>It's Time For Something New!</p>
      <div className="container">
        <div className="row justify-content-center">
          <form className="magic" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Enter Current Password"
                value={currentPassword}
                onChange={handlePasswordChange}
              />
            </div>
            <button
              type="submit"
              className="btn btn-primary"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default PassWordEntry;
