import React, { useState } from "react";
import { random } from "lodash"; // Import random function from lodash library

const PassWordEntry = () => {
  const [currentPassword, setCurrentPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState(false);

  const handlePasswordChange = (e) => {
    setCurrentPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const confirmed = window.confirm(
      `Are you sure you want to create a new password based on "${currentPassword}"?`
    );

    if (confirmed) {
      setConfirmNewPassword(true);
    }
  };

  const generateStrongPassword = (password) => {
    const analyzePassword = (password) => {
      const hasUpper = /[A-Z]/.test(password);
      const hasLower = /[a-z]/.test(password);
      const hasSymbols = /[!@#$%^&*(),.?":{}|<>]/.test(password);
      const hasNumbers = /\d/.test(password);
      const length = password.length;
      return { length, hasUpper, hasLower, hasSymbols, hasNumbers };
    };

    const { length, hasUpper, hasLower, hasSymbols, hasNumbers } =
      analyzePassword(password);

    let characterPool = "";
    if (hasLower) characterPool += "abcdefghijklmnopqrstuvwxyz";
    if (hasUpper) characterPool += "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    if (hasSymbols) characterPool += '!@#$%^&*(),.?":{}|<>';
    if (hasNumbers) characterPool += "0123456789";

    let securePasswordList = [];

    for (let char of password) {
      if (char.match(/[a-z]/)) securePasswordList.push(random(characterPool));
      else if (char.match(/[A-Z]/))
        securePasswordList.push(random(characterPool));
      else if (char.match(/\d/)) securePasswordList.push(random(characterPool));
      else if (char.match(/[!@#$%^&*(),.?":{}|<>]/))
        securePasswordList.push(random(characterPool));
      else securePasswordList.push(char);
    }

    while (securePasswordList.length < length) {
      securePasswordList.push(random(characterPool));
    }

    return securePasswordList.join("");
  };

  const generateWeakPassword = (password) => {
    // Implement weak password generation logic here
    // For example, just return a transformed version of the input password
    return password.toUpperCase();
  };

  const handlePasswordLevelSelect = (level) => {
    const newGeneratedPassword =
      level === "Strong"
        ? generateStrongPassword(currentPassword)
        : generateWeakPassword(currentPassword);

    const confirmed = window.confirm(
      `You selected "${level}" level for password change. Proceed with new password: ${newGeneratedPassword}?`
    );

    if (confirmed) {
      console.log(
        `New password created with "${level}" strength:`,
        newGeneratedPassword
      );
      setCurrentPassword("");
      setConfirmNewPassword(false);
    }
  };

  return (
    <>
      <p>It's Time For Something New!</p>
      <div className="container">
        <div className="row justify-content-center">
          <form className="magic" onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="password"
                className="form-control"
                placeholder="Enter Current Password"
                value={currentPassword}
                onChange={handlePasswordChange}
              />
            </div>
            <button
              type="submit"
              className="btn btn-primary"
              style={{ marginTop: "10px" }}
            >
              Submit
            </button>
          </form>
        </div>
      </div>

      {confirmNewPassword && (
        <div className="password-level-select">
          <h3>Select Password Change Level:</h3>
          <button
            className="btn btn-info"
            onClick={() => handlePasswordLevelSelect("Weak")}
            style={{ marginRight: "10px", marginTop: "10px" }}
          >
            Weak
          </button>
          <button
            className="btn btn-danger"
            onClick={() => handlePasswordLevelSelect("Strong")}
            style={{ marginTop: "10px" }}
          >
            Strong
          </button>
        </div>
      )}
    </>
  );
};

export default PassWordEntry;
